    private void Update_table(){
        try{
        String sql ="select * from korban_jiwa";
        pst=conn.prepareStatement(sql);
        rs=pst.executeQuery();
        table_korban.setModel(DbUtils.resultSetToTableModel(rs));
    }
    catch(Exception e){
        JOptionPane.showMessageDialog(null, e);
    }
}
