    private void table_korbanMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_table_korbanMouseClicked
        DefaultTableModel tabmode=(DefaultTableModel)table_korban.getModel();
        int bar = table_korban.getSelectedRow();   
        String a = tabmode.getValueAt(bar, 0).toString();
        String b = tabmode.getValueAt(bar, 1).toString();
        String c = tabmode.getValueAt(bar, 2).toString();
                
        txt_nik.setText(a);
        txt_nama.setText(b);
        txt_usia.setText(c);
    }//GEN-LAST:event_table_korbanMouseClicked

    private void table_korbanKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_table_korbanKeyPressed
        if (evt.getKeyCode()==KeyEvent.VK_DOWN || evt.getKeyCode()==KeyEvent.VK_UP);
        try{
            int row =table_korban.getSelectedRow();
            String Table_click=(table_korban.getModel().getValueAt(row, 0).toString());
            String sql="select * from korban_jiwa where nik='"+Table_click+"'";
            pst=conn.prepareStatement(sql);
            rs=pst.executeQuery();
            if(rs.next()){
                String add1 =rs.getString("nik");
                txt_nik.setText(add1);
                String add2 =rs.getString("nama");
                txt_nama.setText(add2);
                String add3 =rs.getString("usia");
                txt_usia.setText(add3);
            }
        } catch (Exception e){
            JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_table_korbanKeyPressed

    private void Cmd_saveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Cmd_saveActionPerformed
        try{
            String sql ="insert into korban_jiwa (nik,nama,usia) values (?,?,?)";
            pst=conn.prepareStatement(sql);
            pst.setString(1, txt_nik.getText());
            pst.setString(2, txt_nama.getText());
            pst.setString(3, txt_usia.getText());

            pst.execute();
            JOptionPane.showMessageDialog(null, "Saved");
        }catch(Exception e){
            JOptionPane.showMessageDialog(null, e);
        }
        Update_table();
        datatable();
    }//GEN-LAST:event_Cmd_saveActionPerformed

    private void cmd_clearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmd_clearActionPerformed
        clear();
        datatable();
    }//GEN-LAST:event_cmd_clearActionPerformed

    private void txt_updateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txt_updateActionPerformed
        try {
            String value1 = txt_nik.getText();
            String value2 = txt_nama.getText();
            String value3 = txt_usia.getText();


            String sql ="update korban_jiwa set nik='"+value1+"',nama='"+value2+"',"
                    + "usia='"+value3+"' where nik='"+value1+"'";
            pst=conn.prepareStatement(sql);
            pst.execute();
            JOptionPane.showMessageDialog(null, "Updated");

        }catch (Exception e) {
            JOptionPane.showMessageDialog(null, e);
        }
        Update_table();
        datatable();
    }//GEN-LAST:event_txt_updateActionPerformed

    private void cmd_deleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmd_deleteActionPerformed
        int row =table_korban.getSelectedRow();
        int p = JOptionPane.showConfirmDialog(null,"Apakah Anda Yaqin?","Delete",
                JOptionPane.YES_NO_OPTION);
        if(p==0){
            String Table_click=(table_korban.getModel().getValueAt(row, 0).toString());
            String sql="delete from korban_jiwa where nik='"+Table_click+"'";
            try {
                pst=conn.prepareStatement(sql);
                pst.execute();
                JOptionPane.showMessageDialog(null, "Deleted");

            }catch (Exception e) {
                JOptionPane.showMessageDialog(null, e);
            }
            Update_table();
            datatable();
        }
    }//GEN-LAST:event_cmd_deleteActionPerformed

