    private void datatable(){
        Object[] Baris = {"Kode Laporan","Nama Pelapor","Alamat Kejadian","Kondisi Kejadian","Tanggal"};
        tabmode = new DefaultTableModel(null, Baris);
        table_laporan.setModel(tabmode);
        String sql = "select * from laporan_masuk";
        try {
            java.sql.Statement stat = conn.createStatement();
            ResultSet hasil = stat.executeQuery(sql);
            while (hasil.next()){
                String a = hasil.getString("kode_laporan");
                String b = hasil.getString("nama_lapor");
                String c = hasil.getString("alamat");
                String d = hasil.getString("kondisi");
                String e = hasil.getString("tgl");
                                               
                String[] data = {a,b,c,d,e};
                tabmode.addRow(data);
            }
        }catch(Exception e){
            JOptionPane.showMessageDialog(null, e);
        }
    }

