    private void table_laporanMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_table_laporanMouseClicked
        DefaultTableModel tabmode=(DefaultTableModel)table_laporan.getModel();
        int bar = table_laporan.getSelectedRow();   
        String a = tabmode.getValueAt(bar, 0).toString();
        String b = tabmode.getValueAt(bar, 1).toString();
        String c = tabmode.getValueAt(bar, 2).toString();
        String d = tabmode.getValueAt(bar, 3).toString();
        String e = tabmode.getValueAt(bar, 4).toString();
                
        txt_kode.setText(a);
        txt_nama.setText(b);
        txt_alamat.setText(c);
        txt_kondisi.setText(d);
        txt_date.setDateFormatString(e);
    }//GEN-LAST:event_table_laporanMouseClicked

    private void table_laporanKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_table_laporanKeyPressed
        if (evt.getKeyCode()==KeyEvent.VK_DOWN || evt.getKeyCode()==KeyEvent.VK_UP);
        try{
            int row =table_laporan.getSelectedRow();
            String Table_click=(table_laporan.getModel().getValueAt(row, 0).toString());
            String sql="select * from laporan_masuk where kode_laporan='"+Table_click+"'";
            pst=conn.prepareStatement(sql);
            rs=pst.executeQuery();
            if(rs.next()){
                String add1 =rs.getString("kode_laporan");
                txt_kode.setText(add1);
                String add2 =rs.getString("nama_lapor");
                txt_nama.setText(add2);
                String add3 =rs.getString("alamat");
                txt_alamat.setText(add3);
                String add4 =rs.getString("kondisi");
                txt_kondisi.setText(add4);
                String add5 =rs.getString("tgl");
                txt_date.setDateFormatString(add5);
            }
        } catch (Exception e){
            JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_table_laporanKeyPressed

    private void Cmd_saveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Cmd_saveActionPerformed
        try{
            String sql ="insert into laporan_masuk (kode_laporan,nama_lapor,alamat,kondisi,tgl) values (?,?,?,?,?)";
            pst=conn.prepareStatement(sql);
            pst.setString(1, txt_kode.getText());
            pst.setString(2, txt_nama.getText());
            pst.setString(3, txt_alamat.getText());
            pst.setString(4, txt_kondisi.getText());
            pst.setString(5, ((JTextField)txt_date.getDateEditor().getUiComponent()).getText());
            pst.execute();
            JOptionPane.showMessageDialog(null, "Tersimpan");
        }catch(Exception e){
            JOptionPane.showMessageDialog(null, e);
        }
        Update_table();
        datatable();
    }//GEN-LAST:event_Cmd_saveActionPerformed

