    private void Update_table(){
        try{
        String sql ="select * from mobil";
        pst=conn.prepareStatement(sql);
        rs=pst.executeQuery();
        table_mobil.setModel(DbUtils.resultSetToTableModel(rs));
    }
    catch(Exception e){
        JOptionPane.showMessageDialog(null, e);
    }
}
    public void clear(){
        txt_nomor.setText("");
        txt_jenis.setText("");
        txt_plat.setText("");
    }
