    private void table_mobilMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_table_mobilMouseClicked
        DefaultTableModel tabmode=(DefaultTableModel)table_mobil.getModel();
        int bar = table_mobil.getSelectedRow();   
        String a = tabmode.getValueAt(bar, 0).toString();
        String b = tabmode.getValueAt(bar, 1).toString();
        String c = tabmode.getValueAt(bar, 2).toString();
                
        txt_nomor.setText(a);
        txt_jenis.setText(b);
        txt_plat.setText(c);   
    }//GEN-LAST:event_table_mobilMouseClicked

    private void table_mobilKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_table_mobilKeyPressed
        if (evt.getKeyCode()==KeyEvent.VK_DOWN || evt.getKeyCode()==KeyEvent.VK_UP);
        try{
            int row =table_mobil.getSelectedRow();
            String Table_click=(table_mobil.getModel().getValueAt(row, 0).toString());
            String sql="select * from mobil where nomor='"+Table_click+"'";
            pst=conn.prepareStatement(sql);
            rs=pst.executeQuery();
            if(rs.next()){
                String add1 =rs.getString("nomor");
                txt_nomor.setText(add1);
                String add2 =rs.getString("jenis");
                txt_jenis.setText(add2);
                String add3 =rs.getString("plat");
                txt_jenis.setText(add3);
            }
        } catch (Exception e){
            JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_table_mobilKeyPressed

    private void Cmd_saveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Cmd_saveActionPerformed
        try{
            String sql ="insert into mobil (nomor,jenis,plat) values (?,?,?)";
            pst=conn.prepareStatement(sql);
            pst.setString(1, txt_nomor.getText());
            pst.setString(2, txt_jenis.getText());
            pst.setString(3, txt_plat.getText());
            pst.execute();
            JOptionPane.showMessageDialog(null, "Saved");
        }catch(Exception e){
            JOptionPane.showMessageDialog(null, e);
        }
        Update_table();
        datatable();
    }//GEN-LAST:event_Cmd_saveActionPerformed

