    private void txt_updateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txt_updateActionPerformed

        try {
            String value2 = txt_nomor.getText();
            String value3 = txt_jenis.getText();
            String value4 = txt_plat.getText();

            String sql ="update mobil set nomor='"+value2+"',jenis='"+value3+"',plat='"+value4+"' where nomor='"+value2+"'";
            pst=conn.prepareStatement(sql);
            pst.execute();
            JOptionPane.showMessageDialog(null, "Berhasil Terubah");

        }catch (Exception e) {
            JOptionPane.showMessageDialog(null, e);
        }
        Update_table();
        datatable();
    }//GEN-LAST:event_txt_updateActionPerformed

    private void cmd_deleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmd_deleteActionPerformed
        int row =table_mobil.getSelectedRow();
        int p = JOptionPane.showConfirmDialog(null,"Apakah Anda Yaqin?","Delete",JOptionPane.YES_NO_OPTION);
        if(p==0){
            String Table_click=(table_mobil.getModel().getValueAt(row, 0).toString());
            String sql="delete from mobil where nomor='"+Table_click+"'";
            try {
                pst=conn.prepareStatement(sql);
                pst.execute();
                JOptionPane.showMessageDialog(null, "Deleted");

            }catch (Exception e) {
                JOptionPane.showMessageDialog(null, e);
            }
            Update_table();
            datatable();
        }
    }//GEN-LAST:event_cmd_deleteActionPerformed

